package revisão;

public class LaçosForWhile {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		Criar um laço utilizando while para imprimir de 0 a 10
//		Criar um laço utilizando for para imprimir de 0 a 10

		int variavel =10;
		while(variavel <= 20){
			System.out.println(variavel);
			variavel++;
		}
		
		
		for(int variavel2 = 10; variavel2 <= 20; variavel2++) {
			System.out.println(variavel2);
		}		
	}
}
